Start-Process -Verb RunAs cmd.exe -ArgumentList '/c if not exist "%PROGRAMDATA%\chocolatey\choco.exe" (@powershell -NoProfile -ExecutionPolicy Bypass -Command "iex ((New-Object System.Net.WebClient).DownloadString('https://chocolatey.org/install.ps1'))" && SET "PATH=%PATH%;%ALLUSERSPROFILE%\chocolatey\bin")'
